using System;using System.Collections.Generic;using System.ComponentModel.DataAnnotations;using System.ComponentModel.DataAnnotations.Schema;namespace Asga.Auth{    [Table("Apps", Schema = "Auth")]    public partial class App : AuthModelBase, IAuthModel    {        public App()        {            Roles = new HashSet<Role>();            Users = new HashSet<User>();        }        public long Id { get; set; }        [StringLength(150)]        public string Name { get; set; }        [StringLength(150)]        public string DisplayName { get; set; }        [Column(TypeName = "datetime")]        public DateTime? CreatedOn { get; set; }        public long? CreatedBy { get; set; }        [Column(TypeName = "datetime")]        public DateTime? UpdatedOn { get; set; }        public long? UpdatedBy { get; set; }        [InverseProperty("TenantApp")]        public ICollection<Role> Roles { get; set; }        [InverseProperty("App")]        public ICollection<User> Users { get; set; }    }}