using System;using System.Collections.Generic;using System.ComponentModel.DataAnnotations;using System.ComponentModel.DataAnnotations.Schema;namespace Asga.Mobile{    [Table("Notifications", Schema = "Mob")]    public partial class Notification : AsgaMobileModelBase, IAsgaMobileModel    {        public Notification()        {            UserNotifications = new HashSet<UserNotification>();        }        public long Id { get; set; }        [StringLength(50)]        public string TextId { get; set; }        public string Parameters { get; set; }        [StringLength(50)]        public string EntityType { get; set; }        public long? EntityId { get; set; }        public bool IsSent { get; set; }        [Column(TypeName = "datetime")]        public DateTime? CreatedOn { get; set; }        public long? CreatedBy { get; set; }        [Column(TypeName = "datetime")]        public DateTime? UpdatedOn { get; set; }        public long? UpdatedBy { get; set; }        [StringLength(200)]        public string Text { get; set; }        [StringLength(200)]        public string Title { get; set; }        [InverseProperty("Notification")]        public ICollection<UserNotification> UserNotifications { get; set; }    }}