using System;using Microsoft.EntityFrameworkCore;using Microsoft.EntityFrameworkCore.Metadata;namespace Asga.Public{    public partial class PublicContext : DbContext    {        public PublicContext()        {        }        public PublicContext(DbContextOptions<PublicContext> options)            : base(options)        {        }        public virtual DbSet<AboutSection> AboutSections { get; set; }        public virtual DbSet<ContactItem> ContactItems { get; set; }        public virtual DbSet<ContactItemType> ContactItemTypes { get; set; }        public virtual DbSet<HomeSlide> HomeSlides { get; set; }        protected override void OnModelCreating(ModelBuilder modelBuilder)        {            modelBuilder.Entity<AboutSection>(entity =>            {                entity.Property(e => e.Id).ValueGeneratedNever();            });            modelBuilder.Entity<ContactItem>(entity =>            {                entity.Property(e => e.Id).ValueGeneratedNever();                entity.HasOne(d => d.ContactItemType)                    .WithMany(p => p.ContactItems)                    .HasForeignKey(d => d.ContactItemTypeId)                    .HasConstraintName("FK_ContactItems_ContactItemTypes");            });            modelBuilder.Entity<ContactItemType>(entity =>            {                entity.Property(e => e.Id).ValueGeneratedNever();            });            modelBuilder.Entity<HomeSlide>(entity =>            {                entity.Property(e => e.Id).ValueGeneratedNever();            });        }    }}