using System;using System.Collections.Generic;using System.ComponentModel.DataAnnotations;using System.ComponentModel.DataAnnotations.Schema;namespace Asga.Public{    [Table("MessageTypes", Schema = "Publ")]    public partial class MessageType : AsgaPublicModelBase, IAsgaPublicModel    {        public MessageType()        {            Messages = new HashSet<Message>();        }        public long Id { get; set; }        [StringLength(50)]        public string Name { get; set; }        public int BaseType { get; set; }        [Column(TypeName = "datetime")]        public DateTime? CreatedOn { get; set; }        public long? CreatedBy { get; set; }        [Column(TypeName = "datetime")]        public DateTime? UpdatedOn { get; set; }        public long? UpdatedBy { get; set; }        [InverseProperty("MessageType")]        public ICollection<Message> Messages { get; set; }    }}