using System;using System.Collections.Generic;using System.ComponentModel.DataAnnotations;using System.ComponentModel.DataAnnotations.Schema;namespace Asga.Auth{    [Table("Domains", Schema = "Auth")]    public partial class Domain : Asga.AsgaModelBase, Asga.IAsgaModel    {        public Domain()        {            Resources = new HashSet<Resource>();            TenantDomains = new HashSet<TenantDomain>();        }        public long Id { get; set; }        [StringLength(50)]        public string Name { get; set; }        [Column(TypeName = "datetime")]        public DateTime? CreatedOn { get; set; }        [Column(TypeName = "datetime")]        public DateTime? UpdatedOn { get; set; }        public long? CreatedBy { get; set; }        public long? UpdatedBy { get; set; }        [InverseProperty("Domain")]        public ICollection<Resource> Resources { get; set; }        [InverseProperty("Domain")]        public ICollection<TenantDomain> TenantDomains { get; set; }    }}