using System;using System.Collections.Generic;using System.ComponentModel.DataAnnotations;using System.ComponentModel.DataAnnotations.Schema;namespace Asga.Auth{    [Table("Tenants", Schema = "Auth")]    public partial class Tenant : AsgaModelBase, IAsgaModel    {        public Tenant()        {            Users = new HashSet<User>();        }        public long Id { get; set; }        [Required]        [StringLength(100)]        public string Code { get; set; }        [Required]        [StringLength(200)]        public string Name { get; set; }        [Column(TypeName = "ntext")]        public string Description { get; set; }        [Column(TypeName = "datetime")]        public DateTime? CreatedOn { get; set; }        [Column(TypeName = "datetime")]        public DateTime? UpdatedOn { get; set; }        public long? CreatedBy { get; set; }        public long? UpdatedBy { get; set; }        [InverseProperty("Tenant")]        public ICollection<User> Users { get; set; }    }}